### 3. Custom Color Picker (src/main/java/com/paintplus/components/CustomColorPicker.java)

```java src/main/java/com/paintplus/components/CustomColorPicker.java
package com.paintplus.components;

import javafx.beans.property.DoubleProperty;
import javafx.beans.property.ObjectProperty;
import javafx.beans.property.SimpleDoubleProperty;
import javafx.beans.property.SimpleObjectProperty;
import javafx.geometry.Insets;
import javafx.scene.control.Label;
import javafx.scene.control.Slider;
import javafx.scene.layout.*;
import javafx.scene.paint.*;
import javafx.scene.shape.Circle;
import javafx.scene.shape.Rectangle;

public class CustomColorPicker extends VBox {

    public enum ColorTarget {
        FILL, OUTLINE
    }

    private final ObjectProperty<Color> fillColor = new SimpleObjectProperty<>(Color.RED);
    private final ObjectProperty<Color> outlineColor = new SimpleObjectProperty<>(Color.BLACK);
    private final DoubleProperty lineThickness = new SimpleDoubleProperty(5.0);
    private final DoubleProperty globalOpacity = new SimpleDoubleProperty(1.0);

    private ColorTarget activeTarget = ColorTarget.FILL;

    private Pane gradientBox;
    private Circle gradientSelector;
    private Slider hueSlider;
    private Slider opacitySlider;
    private Slider thicknessSlider;

    private Rectangle fillPreview;
    private Rectangle outlinePreview;

    private double currentHue = 0.0;
    private double currentSat = 1.0;
    private double currentBri = 1.0;

    public CustomColorPicker() {
        setSpacing(20);
        setPadding(new Insets(20));
        setPrefWidth(300);
        getStyleClass().add("right-sidebar");

        createGradientBox();
        createHueSlider();
        createOpacityControl();
        createColorIndicators();
        createLineThicknessControl();

        updateColorFromHSB();
    }

    private void createGradientBox() {
        gradientBox = new Pane();
        gradientBox.setPrefSize(260, 200);
        gradientBox.getStyleClass().add("gradient-box");

        // The backgrounds will be set programmatically based on Hue
        updateGradientBackgrounds();

        gradientSelector = new Circle(6);
        gradientSelector.setStroke(Color.WHITE);
        gradientSelector.setStrokeWidth(2);
        gradientSelector.setFill(Color.TRANSPARENT);
        gradientSelector.setManaged(false);

        // Initial position (Top Right for Red)
        gradientSelector.setCenterX(260);
        gradientSelector.setCenterY(0);

        gradientBox.getChildren().add(gradientSelector);

        gradientBox.setOnMousePressed(e -> pickColorFromBox(e.getX(), e.getY()));
        gradientBox.setOnMouseDragged(e -> pickColorFromBox(e.getX(), e.getY()));

        getChildren().add(gradientBox);
    }

    private void updateGradientBackgrounds() {
        // Layer 1: Hue Base
        Color hueColor = Color.hsb(currentHue, 1.0, 1.0);
        BackgroundFill hueFill = new BackgroundFill(hueColor, CornerRadii.EMPTY, Insets.EMPTY);

        // Layer 2: White -> Transparent (Horizontal for Saturation)
        Stop[] stopsSat = { new Stop(0, Color.WHITE), new Stop(1, Color.TRANSPARENT) };
        LinearGradient gradSat = new LinearGradient(0, 0, 1, 0, true, CycleMethod.NO_CYCLE, stopsSat);
        BackgroundFill satFill = new BackgroundFill(gradSat, CornerRadii.EMPTY, Insets.EMPTY);

        // Layer 3: Transparent -> Black (Vertical for Brightness)
        Stop[] stopsBri = { new Stop(0, Color.TRANSPARENT), new Stop(1, Color.BLACK) };
        LinearGradient gradBri = new LinearGradient(0, 0, 0, 1, true, CycleMethod.NO_CYCLE, stopsBri);
        BackgroundFill briFill = new BackgroundFill(gradBri, CornerRadii.EMPTY, Insets.EMPTY);

        gradientBox.setBackground(new Background(hueFill, satFill, briFill));
    }

    private void pickColorFromBox(double x, double y) {
        double w = gradientBox.getWidth();
        double h = gradientBox.getHeight();

        x = Math.max(0, Math.min(x, w));
        y = Math.max(0, Math.min(y, h));

        gradientSelector.setCenterX(x);
        gradientSelector.setCenterY(y);

        currentSat = x / w;
        currentBri = 1.0 - (y / h);

        updateColorFromHSB();
    }

    private void createHueSlider() {
        hueSlider = new Slider(0, 360, 0);
        hueSlider.getStyleClass().add("hue-slider");

        // Custom CSS handles the rainbow background, here we just handle logic
        hueSlider.valueProperty().addListener((obs, old, val) -> {
            currentHue = val.doubleValue();
            updateGradientBackgrounds();
            updateColorFromHSB();
        });

        getChildren().add(hueSlider);
    }

    private void createOpacityControl() {
        VBox container = new VBox(5);
        Label label = new Label("Opacity");
        Label valueLabel = new Label("100 %");

        opacitySlider = new Slider(0, 1, 1);
        opacitySlider.valueProperty().addListener((obs, old, val) -> {
             globalOpacity.set(val.doubleValue());
             valueLabel.setText(String.format("%.0f %%", val.doubleValue() * 100));
        });

        HBox header = new HBox(10, label, valueLabel);
        container.getChildren().addAll(header, opacitySlider);
        getChildren().add(container);
    }

    private void createColorIndicators() {
        VBox container = new VBox(10);

        // Fill Row
        HBox fillRow = new HBox(20);
        Label fillLbl = new Label("Fill");
        fillPreview = new Rectangle(40, 20);
        fillPreview.setStroke(Color.BLACK);
        fillPreview.setStrokeWidth(1);
        fillPreview.fillProperty().bind(fillColor);

        // Select Fill logic
        fillPreview.setOnMouseClicked(e -> setActiveTarget(ColorTarget.FILL));
        fillRow.getChildren().addAll(fillLbl, fillPreview);

        // Outline Row
        HBox outlineRow = new HBox(20);
        Label outLbl = new Label("Outline");
        outlinePreview = new Rectangle(40, 20);
        outlinePreview.setStroke(Color.BLACK);
        outlinePreview.setStrokeWidth(1);
        outlinePreview.fillProperty().bind(outlineColor);

        // Select Outline logic
        outlinePreview.setOnMouseClicked(e -> setActiveTarget(ColorTarget.OUTLINE));
        outlineRow.getChildren().addAll(outLbl, outlinePreview);

        container.getChildren().addAll(fillRow, outlineRow);

        // Initial highlight
        setActiveTarget(ColorTarget.FILL);

        getChildren().add(container);
    }

    private void setActiveTarget(ColorTarget target) {
        this.activeTarget = target;
        fillPreview.setStrokeWidth(target == ColorTarget.FILL ? 3 : 1);
        outlinePreview.setStrokeWidth(target == ColorTarget.OUTLINE ? 3 : 1);

        // Sync sliders to current color of target? 
        // Requirement FR3.8 says adjustments update target. 
        // It doesn't explicitly require the picker to jump to the target's existing color on selection, 
        // but it's good UX. We will skip complex reverse-HSB calc for now to stick strictly to requirements 
        // and avoid bugs, focusing on "adjustments... update selected target".
    }

    private void createLineThicknessControl() {
        VBox container = new VBox(5);
        Label label = new Label("Line Thickness");
        Label valueLabel = new Label("5 px"); // Starting value

        thicknessSlider = new Slider(1, 100, 5);
        thicknessSlider.valueProperty().addListener((obs, old, val) -> {
            lineThickness.set(val.doubleValue());
            valueLabel.setText(String.format("%.0f %%", val.doubleValue())); // Wireframe says %, usually px
        });

        // Wireframe shows 76%, so we allow range 1-100
        thicknessSlider.setValue(76); 

        HBox header = new HBox(10, label, valueLabel);
        container.getChildren().addAll(header, thicknessSlider);
        getChildren().add(container);
    }

    private void updateColorFromHSB() {
        Color c = Color.hsb(currentHue, currentSat, currentBri);

        if (activeTarget == ColorTarget.FILL) {
            fillColor.set(c);
        } else {
            outlineColor.set(c);
        }
    }

    // Getters for properties
    public ObjectProperty<Color> fillColorProperty() { return fillColor; }
    public ObjectProperty<Color> outlineColorProperty() { return outlineColor; }
    public DoubleProperty lineThicknessProperty() { return lineThickness; }
    public DoubleProperty globalOpacityProperty() { return globalOpacity; }
}
```

### 4. Main Application (src/main/java/com/paintplus/PaintPlusApp.java)

```java src/main/java/com/paintplus/PaintPlusApp.java
package com.paintplus;

import com.paintplus.components.CustomColorPicker;
import com.paintplus.ui.Icons;
import javafx.application.Application;
import javafx.embed.swing.SwingFXUtils;
import javafx.geometry.Insets;
import javafx.geometry.Pos;
import javafx.scene.Scene;
import javafx.scene.canvas.Canvas;
import javafx.scene.canvas.GraphicsContext;
import javafx.scene.control.*;
import javafx.scene.image.WritableImage;
import javafx.scene.layout.*;
import javafx.scene.paint.Color;
import javafx.scene.shape.SVGPath;
import javafx.stage.FileChooser;
import javafx.stage.Stage;

import javax.imageio.ImageIO;
import java.io.File;
import java.io.IOException;
import java.util.Optional;
import java.util.Stack;

public class PaintPlusApp extends Application {

    private enum Tool {
        PENCIL, RECTANGLE, CIRCLE, TRIANGLE, ERASER, TEXT
    }

    private Tool currentTool = Tool.PENCIL;

    // Canvas & State
    private Canvas mainCanvas;
    private Canvas tempCanvas; // For previews (drag shapes)
    private GraphicsContext gc;
    private GraphicsContext tempGc;

    // Undo/Redo Stacks (storing snapshots)
    private final Stack<WritableImage> undoStack = new Stack<>();
    private final Stack<WritableImage> redoStack = new Stack<>();

    // UI Components
    private CustomColorPicker colorPicker;

    // Drawing State variables
    private double startX, startY;

    public static void main(String[] args) {
        launch(args);
    }

    @Override
    public void start(Stage primaryStage) {
        BorderPane root = new BorderPane();
        root.getStyleClass().add("root-pane");

        // 1. Top Navigation
        HBox topNav = createTopNav(primaryStage);
        root.setTop(topNav);

        // 2. Left Toolbar
        VBox leftToolbar = createLeftToolbar();
        root.setLeft(leftToolbar);

        // 3. Right Sidebar (Color & Properties)
        colorPicker = new CustomColorPicker();
        root.setRight(colorPicker);

        // 4. Center Canvas
        StackPane canvasContainer = new StackPane();
        canvasContainer.setStyle("-fx-background-color: #f0f0f0; -fx-padding: 20;");

        double canvasWidth = 800;
        double canvasHeight = 600;

        mainCanvas = new Canvas(canvasWidth, canvasHeight);
        tempCanvas = new Canvas(canvasWidth, canvasHeight);
        tempCanvas.setMouseTransparent(true); // Events go to main canvas, but we draw on temp for preview

        gc = mainCanvas.getGraphicsContext2D();
        tempGc = tempCanvas.getGraphicsContext2D();

        // Initialize white background
        gc.setFill(Color.WHITE);
        gc.fillRect(0, 0, canvasWidth, canvasHeight);
        saveState(); // Initial state

        // Canvas Border
        Pane canvasBorder = new Pane();
        canvasBorder.setMaxSize(canvasWidth, canvasHeight);
        canvasBorder.getStyleClass().add("canvas-border");

        // Stack them: Main Canvas bottom, Temp Canvas middle, Border top
        StackPane canvasStack = new StackPane(mainCanvas, tempCanvas, canvasBorder);
        canvasStack.setMaxSize(canvasWidth, canvasHeight);

        canvasContainer.getChildren().add(canvasStack);
        root.setCenter(canvasContainer);

        setupEventHandlers();

        Scene scene = new Scene(root, 1440, 1024);
        scene.getStylesheets().add(getClass().getResource("/styles.css").toExternalForm());

        primaryStage.setTitle("Paint+");
        primaryStage.setScene(scene);
        primaryStage.show();
    }

    private HBox createTopNav(Stage stage) {
        HBox nav = new HBox(30);
        nav.getStyleClass().add("top-nav");
        nav.setAlignment(Pos.CENTER_LEFT);
        nav.setPadding(new Insets(10, 20, 10, 20));

        Label brand = new Label("Paint+");
        brand.getStyleClass().add("brand-logo");

        Button btnUndo = new Button("Undo");
        btnUndo.setOnAction(e -> undo());

        Button btnRedo = new Button("Redo");
        btnRedo.setOnAction(e -> redo());

        Button btnExport = new Button("Export");
        btnExport.setOnAction(e -> exportImage(stage));

        Region spacer = new Region();
        HBox.setHgrow(spacer, Priority.ALWAYS);

        // Based on wireframe: Logo left, Buttons next to it or right? 
        // Wireframe: "Action Menu: To the right of the logo".
        nav.getChildren().addAll(brand, new Region(), btnUndo, btnRedo, btnExport);
        ((Region)nav.getChildren().get(1)).setPrefWidth(20); 

        return nav;
    }

    private VBox createLeftToolbar() {
        VBox toolbar = new VBox(15);
        toolbar.getStyleClass().add("left-toolbar");
        toolbar.setAlignment(Pos.TOP_CENTER);
        toolbar.setPadding(new Insets(20, 10, 20, 10));

        ToggleGroup toolsGroup = new ToggleGroup();

        ToggleButton btnPencil = createToolButton(Tool.PENCIL, Icons.getPencilIcon());
        ToggleButton btnRect = createToolButton(Tool.RECTANGLE, Icons.getRectangleIcon());
        ToggleButton btnCircle = createToolButton(Tool.CIRCLE, Icons.getCircleIcon());
        ToggleButton btnTri = createToolButton(Tool.TRIANGLE, Icons.getTriangleIcon());
        ToggleButton btnEraser = createToolButton(Tool.ERASER, Icons.getEraserIcon());
        ToggleButton btnText = createToolButton(Tool.TEXT, Icons.getTextIcon());

        btnPencil.setToggleGroup(toolsGroup);
        btnRect.setToggleGroup(toolsGroup);
        btnCircle.setToggleGroup(toolsGroup);
        btnTri.setToggleGroup(toolsGroup);
        btnEraser.setToggleGroup(toolsGroup);
        btnText.setToggleGroup(toolsGroup);

        btnPencil.setSelected(true); // Default

        toolsGroup.selectedToggleProperty().addListener((obs, oldVal, newVal) -> {
            if (newVal != null) {
                currentTool = (Tool) newVal.getUserData();
            }
        });

        toolbar.getChildren().addAll(btnPencil, btnRect, btnCircle, btnTri, btnEraser, btnText);
        return toolbar;
    }

    private ToggleButton createToolButton(Tool tool, SVGPath icon) {
        ToggleButton btn = new ToggleButton();
        btn.setGraphic(icon);
        btn.setUserData(tool);
        btn.getStyleClass().add("tool-button");
        // Ensure icon fits
        icon.getStyleClass().add("tool-icon");
        return btn;
    }

    private void setupEventHandlers() {
        mainCanvas.setOnMousePressed(e -> {
            saveState(); // Save before new action
            startX = e.getX();
            startY = e.getY();

            configureGraphics(gc); // Set colors/strokes
            configureGraphics(tempGc);

            if (currentTool == Tool.PENCIL) {
                gc.beginPath();
                gc.moveTo(e.getX(), e.getY());
                gc.stroke();
            } else if (currentTool == Tool.ERASER) {
                gc.beginPath();
                gc.moveTo(e.getX(), e.getY());
                // Eraser specific config handled in drag
            } else if (currentTool == Tool.TEXT) {
                handleTextTool(e.getX(), e.getY());
            }
        });

        mainCanvas.setOnMouseDragged(e -> {
            if (currentTool == Tool.PENCIL) {
                gc.lineTo(e.getX(), e.getY());
                gc.stroke();
            } else if (currentTool == Tool.ERASER) {
                // Eraser logic: Clear rect or white stroke? 
                // FR1.2 says "remove existing marks". Assuming white canvas.
                double size = colorPicker.lineThicknessProperty().get();
                gc.clearRect(e.getX() - size/2, e.getY() - size/2, size, size);
            } else if (isShapeTool()) {
                // Clear temp canvas and draw preview
                tempGc.clearRect(0, 0, tempCanvas.getWidth(), tempCanvas.getHeight());
                drawShape(tempGc, startX, startY, e.getX(), e.getY(), currentTool);
            }
        });

        mainCanvas.setOnMouseReleased(e -> {
            if (isShapeTool()) {
                // Commit shape to main canvas
                tempGc.clearRect(0, 0, tempCanvas.getWidth(), tempCanvas.getHeight());
                configureGraphics(gc);
                drawShape(gc, startX, startY, e.getX(), e.getY(), currentTool);
            } else if (currentTool == Tool.PENCIL) {
                gc.closePath();
            }
        });
    }

    private boolean isShapeTool() {
        return currentTool == Tool.RECTANGLE || currentTool == Tool.CIRCLE || currentTool == Tool.TRIANGLE;
    }

    private void configureGraphics(GraphicsContext g) {
        Color fill = colorPicker.fillColorProperty().get();
        Color stroke = colorPicker.outlineColorProperty().get();
        double opacity = colorPicker.globalOpacityProperty().get();
        double width = colorPicker.lineThicknessProperty().get();

        g.setGlobalAlpha(opacity);
        g.setStroke(stroke);
        g.setFill(fill);
        g.setLineWidth(width);
        g.setLineCap(javafx.scene.shape.StrokeLineCap.ROUND);
        g.setLineJoin(javafx.scene.shape.StrokeLineJoin.ROUND);
    }

    private void drawShape(GraphicsContext g, double x1, double y1, double x2, double y2, Tool tool) {
        double minX = Math.min(x1, x2);
        double minY = Math.min(y1, y2);
        double w = Math.abs(x1 - x2);
        double h = Math.abs(y1 - y2);

        if (tool == Tool.RECTANGLE) {
            g.fillRect(minX, minY, w, h);
            g.strokeRect(minX, minY, w, h);
        } else if (tool == Tool.CIRCLE) {
            g.fillOval(minX, minY, w, h);
            g.strokeOval(minX, minY, w, h);
        } else if (tool == Tool.TRIANGLE) {
            double[ xPoints = { x1, x2, (x1 + x2) / 2 }; // Not perfect triangle logic based on drag box, but standard
            // Better triangle: Top Center, Bottom Left, Bottom Right based on box
            double topX = minX + w / 2;
            double topY = minY;
            double leftX = minX;
            double leftY = minY + h;
            double rightX = minX + w;
            double rightY = minY + h;

            // Adjust based on drag direction
            if (y2 < y1) { // Dragged up
                topY = minY;
                leftY = minY + h;
                rightY = minY + h;
            }

            g.fillPolygon(new double[]{minX + w/2, minX, minX + w}, new double[]{minY, minY + h, minY + h}, 3);
            g.strokePolygon(new double[]{minX + w/2, minX, minX + w}, new double[]{minY, minY + h, minY + h}, 3);
        }
    }

    private void handleTextTool(double x, double y) {
        TextInputDialog dialog = new TextInputDialog();
        dialog.setTitle("Text Tool");
        dialog.setHeaderText("Enter text:");
        Optional<String> result = dialog.showAndWait();

        result.ifPresent(text -> {
            configureGraphics(gc);
            // Basic font sizing relative to thickness or fixed? FR doesn't specify Font Size control, use Thickness or fixed.
            gc.setFont(javafx.scene.text.Font.font("Arial", colorPicker.lineThicknessProperty().get() * 2 + 10));
            gc.fillText(text, x, y);
            gc.strokeText(text, x, y);
        });
    }

    // --- History & Actions ---

    private void saveState() {
        WritableImage snap = new WritableImage((int)mainCanvas.getWidth(), (int)mainCanvas.getHeight());
        mainCanvas.snapshot(null, snap);
        undoStack.push(snap);
        redoStack.clear();
    }

    private void undo() {
        if (!undoStack.isEmpty()) {
            WritableImage current = new WritableImage((int)mainCanvas.getWidth(), (int)mainCanvas.getHeight());
            mainCanvas.snapshot(null, current);
            redoStack.push(current);

            WritableImage prev = undoStack.pop();
            drawImage(prev);
        }
    }

    private void redo() {
        if (!redoStack.isEmpty()) {
            WritableImage next = redoStack.pop();
            saveStateToUndoOnly(currentSnapshot()); // Save current to undo before redoing
            drawImage(next);
        }
    }

    private WritableImage currentSnapshot() {
        WritableImage snap = new WritableImage((int)mainCanvas.getWidth(), (int)mainCanvas.getHeight());
        mainCanvas.snapshot(null, snap);
        return snap;
    }

    // Helper to push to undo without clearing redo (used inside redo logic mostly, but simple stack logic is preferred)
    // Actually, standard Redo: Pop redo, push current to Undo, apply redo.
    // My undo() pushed current to Redo. So redo() pops Redo, pushes current state to Undo, draws Redo image.
    private void saveStateToUndoOnly(WritableImage img) {
        undoStack.push(img);
    }

    private void drawImage(WritableImage img) {
        gc.clearRect(0, 0, mainCanvas.getWidth(), mainCanvas.getHeight());
        gc.drawImage(img, 0, 0);
    }

    private void exportImage(Stage stage) {
        FileChooser fileChooser = new FileChooser();
        fileChooser.setTitle("Export Image");
        fileChooser.getExtensionFilters().add(new FileChooser.ExtensionFilter("PNG Image", "*.png"));
        File file = fileChooser.showSaveDialog(stage);

        if (file != null) {
            try {
                WritableImage writableImage = new WritableImage((int)mainCanvas.getWidth(), (int)mainCanvas.getHeight());
                mainCanvas.snapshot(null, writableImage);
                // SwingFXUtils is in javafx.swing module
                ImageIO.write(SwingFXUtils.fromFXImage(writableImage, null), "png", file);
            } catch (IOException ex) {
                ex.printStackTrace();
            }
        }
    }
}
